function buildSpeechlet(title, outputSpeech, repromptText, shouldEndSession) {
  return {
    outputSpeech: {
      type: "PlainText",
      text: outputSpeech
    },
    card: {
      type: "Simple",
      title: "SessionSpeechlet - " + title,
      content: "SessionSpeechlet - " + outputSpeech
    },
    reprompt: {
      outputSpeech: {
        type: "PlainText",
        text: repromptText
      }
    },
    shouldEndSession: shouldEndSession
  };
}

function buildErrorResponse() {
  return buildSpeechlet('Error', 'Sorry there was an issue with your request', 'Would you like anything else?', false);
}

module.exports = {
  buildResponse: buildResponse,
  buildErrorResponse: buildErrorResponse
}
